import React from 'react';
import { Comment, Avatar, Form, Button, List, Input, Modal, Icon, message, Tooltip } from 'antd';
import moment from 'moment';
import API from "../../../../middleware/api"

const { TextArea } = Input;
const userDetails = localStorage.getItem('user')


const Editor = ({ onChange, onSubmit, submitting, value }) => (
    <div>
        <Form.Item>
            <TextArea rows={4} onChange={onChange} value={value} />
        </Form.Item>
        <Form.Item>
            <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
                <Icon type="message" key="skeleton-message" /> Add Comment
      </Button>
        </Form.Item>
    </div>
);
export default class CommentModal extends React.Component {
    constructor(props) {
        super(props);
        console.log("CM", this.props)
        this.state = {
            comments: [],
            submitting: false,
            value: '',
        };
    }
    componentWillMount() {
        this.getComments()
    }
    getComments = async () => {
        let response = await API.post('/events/getComments', { event_id: this.props.event.id })
        // response.data.code === 200 ? message.success(response.data.msg, 4) : message.error(response.data.msg, 4);    
        this.setState({ comments: response.data.response, eventLoading: false }, () => {
            console.log("Comments", this.state.comments.length)
        }

        )

    }
    handleSubmit = () => {
        if (!this.state.value) {
            return;
        }

        this.setState({
            submitting: true,
        });

        setTimeout(() => {
            this.setState({
                submitting: false,
                value: '',
                comments: [
                    {
                        author: this.props.userDetail.user_name,
                        avatar: this.props.userDetail.image,
                        content: <p>{this.state.value}</p>,
                        datetime: moment().fromNow(),
                    },
                    ...this.state.comments,
                ],
            });
        }, 1000);
    };

    handleChange = e => {
        this.setState({
            value: e.target.value,
        });
    };
    render() {
        const { visible, confirmLoading, name, userDetail } = this.props;
        const { comments, submitting, value } = this.state;
        return (
            <Modal
                width={900}
                title={name}
                visible={visible}
                onOk={this.props.handleOk}
                confirmLoading={confirmLoading}
                onCancel={this.props.handleCancel}
            >
                <div>
                    <Card
                        title={<Meta
                            avatar={<Avatar src={item.user.image} />}
                            title={item.user.user_name}
                            description="a few second ago"
                        />}
                        style={{ width: 600 }}
                    >

                        <Card
                            style={{ width: 552 }}
                            className="main-wrap-card"
                            cover={
                                <img
                                    alt="example"
                                    src={item.images}
                                />
                            }
                            actions={[
                                <span key="comment-basic-like">
                                    <Tooltip title="Like">
                                        <Icon type="like-o" key="skeleton-star-o" />
                                    </Tooltip>
                                    <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                                </span>,
                                <span key="comment-basic-like">
                                    <Tooltip title="DisLike">
                                        <Icon type="dislike-o" key="skeleton-like-o" />
                                    </Tooltip>
                                    <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                                </span>,
                                <span key="comment-basic-like">
                                    <Tooltip title="Comment">
                                        <Icon type="message" key="skeleton-message" onClick={() => this.showModal(item)} />
                                    </Tooltip>
                                    <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                                </span>,
                            ]}
                        >
                            <Meta
                                // avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                                title={item.name}
                                description={item.description}
                            />

                            <Progress percent={30} size="small" />
                        </Card>
                    </Card>
                    <Comment
                        avatar={
                            <Avatar
                                src={userDetail.image}
                                alt={userDetail.user_name}
                            />
                        }
                        content={
                            <Editor
                                onChange={this.handleChange}
                                onSubmit={this.handleSubmit}
                                submitting={submitting}
                                value={value}
                            />
                        }
                    />
                    {comments.length > 0 && <List
                        dataSource={comments}
                        header={`${comments.length} ${comments.length > 1 ? 'comments' : 'comment'}`}
                        itemLayout="horizontal"
                        renderItem={item =>
                            <Comment
                                author={item.user.user_name}
                                avatar={
                                    <Avatar
                                        src={item.user.image}
                                        alt={item.user.user_name}
                                    />
                                }
                                content={
                                    <p>
                                        {item.comment}
                                    </p>
                                }
                                datetime={
                                    <Tooltip title={moment((item.createdAt).year).format('YYYY-MM-DD HH:mm:ss')}>
                                        <span>{moment().fromNow()}</span>
                                    </Tooltip>
                                }
                            />}
                    />
                    }
                </div>
            </Modal>
        );
    }
}
