import React, { Component, Fragment } from "react";
import 'antd/dist/antd.css';
import { Menu, Icon, Avatar } from 'antd';
import "../Navbar/Navbar.css"
import { Redirect } from "react-router-dom";
import {
  Link
} from "react-router-dom";
import API from '../../middleware/api';

const { SubMenu } = Menu;
// import { WrappedLogin } from '../Login/Login'
export default class Navbar extends Component {
  constructor() {
    super();
    this.state = {
      current: 'mail',
      isLogged: false
    };
  }
  componentWillMount() {
    let isLogged = localStorage.getItem('user_id')
    if (isLogged) {

       API.post('/users/signin',{user_id:isLogged}).then(
        response=>
        {
          let userDetails=response.data.response
          this.setState({ isLogged: true ,userDetails},()=>{console.log(this.state)})
        }
      )

    }
  }
  logOut=()=>
  {
    localStorage.removeItem("mytime");

  }
  handleClick = e => {
    console.log('click ', e);
    this.setState({
      current: e.key,
    }, () => {
      console.log(this.state)
    });
  };
  conditionalRender() {
    const { userDetails } = this.state
      return(
        <SubMenu
          title={
            <Avatar src={userDetails.img} />
          }
        >
          <Menu.ItemGroup title={userDetails.user_name}>
            <Menu.Item key="settings">Settings</Menu.Item>
            {/* <Menu.Item key="setting:2">Option 2</Menu.Item> */}
          </Menu.ItemGroup>
          {/* <Menu.ItemGroup title="Item 2"> */}
          <Menu.Item key="logout" onClick={this.logOut}>Logout</Menu.Item>
          {/* <Menu.Item key="setting:4">Option 4</Menu.Item> */}
          {/* </Menu.ItemGroup> */}
        </SubMenu>
      )
  }
  // selectedKeys={[this.state.current]} 
  render() {

    return (
      <Menu onClick={this.handleClick} mode="horizontal">
        {this.conditionalRender()}

      </Menu>
    )
  }
}
