import React from 'react';
import { Comment, Avatar, Form, Button, List, Input, Modal,Icon ,message} from 'antd';
import moment from 'moment';
import API from "../../../../middleware/api"
const { TextArea } = Input;
const userDetails=localStorage.getItem('user')

const CommentList = ({ comments }) => (
    <List
        dataSource={comments}
        header={`${comments.length} ${comments.length > 1 ? 'comments' : 'comment'}`}
        itemLayout="horizontal"
        renderItem={props => <Comment {...props} />}
    />
);

const Editor = ({ onChange, onSubmit, submitting, value }) => (
    <div>
        <Form.Item>
            <TextArea rows={4} onChange={onChange} value={value} />
        </Form.Item>
        <Form.Item>
            <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
            <Icon type="message" key="skeleton-message" /> Add Comment
      </Button>
        </Form.Item>
    </div>
);
export default class CommentModal extends React.Component {
    constructor(props) {
        super(props);
        console.log("CM", this.props)
        this.state = {
            comments: [],
            submitting: false,
            value: '',
        };
    }
    componentWillMount() {
        this.getComments()
      }
      getComments = async () => {
        let response = await API.post('/events/getComments',{event_id:this.props.event.id})
        response.data.code === 200 ? message.success(response.data.msg, 4) : message.error(response.data.msg, 4);
        console.log("commnets", response.data.response)

        this.setState({ comments: response.data.response, eventLoading: false })

      }
    handleSubmit = () => {
        if (!this.state.value) {
          return;
        }

        this.setState({
          submitting: true,
        });

        setTimeout(() => {
          this.setState({
            submitting: false,
            value: '',
            comments: [
              {
                name: 'Han Solo',
                avatar: this.props.user.image,
                content: <p>{this.state.value}</p>,
                datetime: moment().fromNow(),
              },
              ...this.state.comments,
            ],
          });
        }, 1000);
      };

      handleChange = e => {
        this.setState({
          value: e.target.value,
        });
      };
    render() {
        const { visible, confirmLoading ,name} = this.props;
        const { comments, submitting, value } = this.state;
        return (
            <Modal
            width={900}
                title={name}
                visible={visible}
                onOk={this.props.handleOk}
                confirmLoading={confirmLoading}
                onCancel={this.props.handleCancel}
            >
                <div>
                    <Comment
                        avatar={
                            <Avatar
                                src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                                alt={userDetails.fir}
                            />
                        }
                        content={
                            <Editor
                                onChange={this.handleChange}
                                onSubmit={this.handleSubmit}
                                submitting={submitting}
                                value={value}
                            />
                        }
                    />
                    {comments.length > 0 && <CommentList comments={comments} />}
                </div>
            </Modal>
        );
    }
}
