import { Skeleton, Switch, List, Avatar, Icon, Tooltip, Progress, message, Typography, Tag, Divider, Card } from 'antd';
import React from 'react';
import CommentModal from '../Feed/CommentModal';
import { AddEvent } from '../AddEvent'
import API from "../../../../middleware/api"
const { Text } = Typography;
const { Meta } = Card;
export default class CardContainer extends React.Component {
  state = {
    eventLoading: true,
    visible: false,
    confirmLoading: false,
    likes: 123,
    name: "hello"
  };
  componentWillMount() {
    this.getAllEvents()
  }
  getAllEvents = async () => {
    let response = await API.get('/events/getEvents')
    response.data.code === 200 ? message.success(response.data.msg, 4) : message.error(response.data.msg, 4);
    console.log("res", response.data.response)

    this.setState({ events: response.data.response, eventLoading: false })

  }
  onChange = checked => {
    this.setState({ loading: !checked });
  };
  showModal = () => {
    this.setState({
      visible: true,
    });
  };
  handleOk = () => {
    this.setState({
      ModalText: 'The modal will be closed after two seconds',
      confirmLoading: true,
    });
    setTimeout(() => {
      this.setState({
        visible: false,
        confirmLoading: false,
      });
    }, 2000);
  };

  handleCancel = () => {
    console.log('Clicked cancel button');
    this.setState({
      visible: false,
    });
  };
  render() {
    const { eventLoading, visible, confirmLoading, likes, name, events } = this.state;

    return (
      [<AddEvent/>,
      <List
        itemLayout="horizontal"
        style={{margin:'0 auto', width: 600}}
        dataSource={events}
        renderItem={item => (
          <List.Item>
            <Skeleton loading={eventLoading} active avatar>
              <Card
                title={<Meta
                  avatar={<Avatar src={item.user.image} />}
                  title={item.user.user_name}
                  description="a few second ago"
                />}
                style={{ width: 600 }}
              >

                <Card
                  style={{ width: 552 }}
                  className="main-wrap-card"
                  cover={
                    <img
                      alt="example"
                      src={item.images}
                    />
                  }
                  actions={[
                    <span key="comment-basic-like">
                      <Tooltip title="Like">
                        <Icon type="like-o" key="skeleton-star-o" />
                      </Tooltip>
                      <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                    </span>,
                    <span key="comment-basic-like">
                      <Tooltip title="DisLike">
                        <Icon type="dislike-o" key="skeleton-like-o" />
                      </Tooltip>
                      <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                    </span>,
                    <span key="comment-basic-like">
                      <Tooltip title="Comment">
                        <Icon type="message" key="skeleton-message" onClick={this.showModal} />
                      </Tooltip>
                      <span style={{ paddingLeft: 8, cursor: 'auto' }}>{likes}</span>
                    </span>,
                  ]}
                >
                  <Meta
                    // avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                    title={item.name}
                    description={item.description}
                  />

                    <Progress percent={30} size="small" />
                </Card>
                <CommentModal name={item.name} user={t} event={item} showModal={this.showModal} handleCancel={this.handleCancel} confirmLoading={confirmLoading} visible={visible} handleOk={this.handleOk} />
              </Card>
            </Skeleton>
          </List.Item>)}
      />
      ]
    );
  }
}
