import { Steps, Button, message,Icon,Layout } from 'antd';
import { WrappedSignin } from '../Signin/Signin';
import { WrappedVerifyMail } from '../Signin/VerifyMail';
import React, { Component } from 'react';
const { Step } = Steps;
const { Header, Content, Footer, Sider } = Layout;

const steps = [
    {
        title: 'Login',
        content: <WrappedSignin ref={this.WrappedSignin}/>,
        icon: <Icon type="user" />
    },
    {
        title: 'Verification',
        content: <WrappedVerifyMail ref={this.WrappedVerifyMail}/>,
        icon: <Icon type="solution" />
    }
];
export default class Stepper extends React.Component {
    constructor(props) {
        super(props);
        this.WrappedSignin = React.createRef();
    this.WrappedVerifyMail = React.createRef();
        this.state = {
            current: 0,
        };
    }

    next() {
        const current = this.state.current + 1;
        this.WrappedSignin.current.onSubmit();
        this.setState({ current });
    }

    prev() {
        const current = this.state.current - 1;
        this.setState({ current });
    }

    render() {
        const { current } = this.state;
        return (
            <Layout>
                <Header style={{ background: '#fff', padding:'50px 100px' }}>
                    <Steps current={current}>
                        {steps.map(item => (
                            <Step key={item.title} title={item.title} icon={item.icon}/>
                        ))}
                    </Steps>
                </Header>
                <Layout style={{ background: '#ffffff' }}>
                    <Content style={{ background: '#fff', padding: 24, margin: ' 0 auto', minHeight: 280, }}>

                        <div className="steps-content">{
                            cur
                        }
                        </div>
                        <div className="steps-action">
                            {current < steps.length - 1 && (
                                <Button type="primary" onClick={() => this.next()}>
                                    Next
            </Button>
                            )}
                            {current === steps.length - 1 && (
                                <Button type="primary" onClick={() => message.success('Processing complete!')}>
                                    Done
            </Button>
                            )}
                            {current > 0 && (
                                <Button style={{ marginLeft: 8 }} onClick={() => this.prev()}>
                                    Previous
            </Button>
                            )}
                        </div>               
            </Content>
            </Layout>
    </Layout >
    );
    }
}
